SELECT e.podcast_id, e.episode_id, e.description as episode_description, e.link as episode_link, e.media_url as episode_media_url, e.media_type as episode_media_type, e.title as episode_title, e.publication_date as episode_pub_date, e.title_in_url as episode_title_in_url, e.is_new, p.title_in_url as podcast_title_in_url, p.url as podcast_url, p.podcast_image_url, p.title as podcast_title, p.identifier as podcast_identifier, p.podcast_link as podcast_link, p.social_fb_page, p.social_twitter_page, p.social_gplus_page FROM episodes e, podcasts p WHERE e.podcast_id = #{podcastId} AND e.podcast_id=p.podcast_id AND e.availability = 200 ORDER BY e.publication_date DESC
LIMIT #{offset}, #{count}
LIMIT #{count}

SELECT e.podcast_id, e.episode_id, e.description as episode_description, e.link as episode_link, e.media_url as episode_media_url, e.media_type as episode_media_type, e.title as episode_title, e.publication_date as episode_pub_date, e.title_in_url as episode_title_in_url, e.is_new, p.title_in_url as podcast_title_in_url, p.url as podcast_url, p.podcast_image_url, p.title as podcast_title, p.identifier as podcast_identifier, p.podcast_link as podcast_link, p.social_fb_page, p.social_twitter_page, p.social_gplus_page FROM episodes e, podcasts p WHERE e.podcast_id = p.podcast_id AND p.identifier = #{name} AND e.availability = 200 ORDER BY e.publication_date DESC
LIMIT #{offset}, #{count}
LIMIT #{count}
